import React from 'react';
import { useState } from 'react';
import './App.css';
import SingleCard from './components/Card/SingleCard';

const cardImg = [
  {'src': './img/helmet-1.png'},
  {'src': './img/potion-1.png'},
  {'src': './img/ring-1.png'},
  {'src': './img/scroll-1.png'},
  {'src': './img/shield-1.png'},
  {'src': './img/sword-1.png'},
];

function App() {
  const [cards, setCards] = useState([]);
  const [count, setCount] = useState(0);
  const [choiseCard1, setChoiseCard1] = useState(null);
  const [choiseCard2, setChoiseCard2] = useState(null);

  const shuffleCards = () => {
    let shuffledCards = [...cardImg, ...cardImg];
    shuffledCards = shuffledCards.sort(() => Math.random() - 0.5)
    .map((item => {
      return {...item, 
              'id': Math.random()
            }
    }))
    setCards(shuffledCards);
    setCount(0)
  };

  const setChoiceCard = (card) => {
    if (choiseCard1) {
      setChoiseCard2(card)
    } else {
      setChoiseCard1(card)
    }
  };

  const resetTurn = () => {
    setChoiseCard1(null);
    setChoiseCard2(null);
    setCount((prevCount) => {});
  };

  return (
    <div className="App">
      <h1>Magic Match</h1>
      <button onClick={shuffleCards}>New Game</button>
      <div className='card-grid'>
        {cards.map(card => (
          <SingleCard card={card} key={card.id} setChoiceCard={setChoiceCard}/>
        ))}
      </div>
    </div>
  );
}

export default App

